import React, { useState, useEffect } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import { cycleService } from '../services/cycleService';
import { clientService } from '../services/clientService';
import LoadingSpinner from './common/LoadingSpinner';
import { useAuth } from '../contexts/AuthContext';

const CycleForm = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const location = useLocation();
  const isEdit = Boolean(id);
  const clientIdFromState = location.state?.clientId;

  const [formData, setFormData] = useState({
    clientId: clientIdFromState || '',
    targetAmount: '',
    dailyContribution: '',
    startDate: '',
    endDate: ''
  });

  const [clients, setClients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const { user } = useAuth();

  useEffect(() => {
    fetchClients();
    if (isEdit) {
      fetchCycle();
    }
  }, [id, isEdit, user]);

  const fetchClients = async () => {
    try {
      let response;
      if (user?.role === 'COLLECTOR') {
        response = await clientService.getClientsByCollector(user.id);
      } else {
        response = await clientService.getAllClients();
      }
      setClients(response.data);
    } catch (error) {
      console.error('Error fetching clients:', error);
    }
  };

  const fetchCycle = async () => {
    try {
      setLoading(true);
      const response = await cycleService.getCycleById(id);
      const cycle = response.data;
      setFormData({
        clientId: cycle.client?.id || '',
        targetAmount: cycle.targetAmount || '',
        dailyContribution: cycle.dailyContribution || '',
        startDate: cycle.startDate || '',
        endDate: cycle.endDate || ''
      });
    } catch (error) {
      console.error('Error fetching cycle:', error);
      alert('Error fetching cycle details. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.clientId) {
      newErrors.clientId = 'Client is required';
    }

    if (!formData.targetAmount || parseFloat(formData.targetAmount) <= 0) {
      newErrors.targetAmount = 'Target amount must be greater than 0';
    }

    if (!formData.dailyContribution || parseFloat(formData.dailyContribution) <= 0) {
      newErrors.dailyContribution = 'Daily contribution must be greater than 0';
    }

    if (!formData.startDate) {
      newErrors.startDate = 'Start date is required';
    }

    if (!formData.endDate) {
      newErrors.endDate = 'End date is required';
    }

    if (formData.startDate && formData.endDate && new Date(formData.startDate) >= new Date(formData.endDate)) {
      newErrors.endDate = 'End date must be after start date';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      setLoading(true);

      const cycleData = {
        clientId: parseInt(formData.clientId),
        targetAmount: parseFloat(formData.targetAmount),
        dailyContribution: parseFloat(formData.dailyContribution),
        startDate: formData.startDate,
        endDate: formData.endDate
      };

      if (isEdit) {
        await cycleService.updateCycle(id, cycleData);
        alert('Cycle updated successfully');
      } else {
        await cycleService.createCycle(cycleData);
        alert('Cycle created successfully');
      }

      navigate('/cycles');
    } catch (error) {
      console.error('Error saving cycle:', error);
      alert('Error saving cycle. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const getClientName = (clientId) => {
    const client = clients.find(c => c.id === parseInt(clientId));
    return client ? client.name : '';
  };

  if (loading && isEdit) {
    return <LoadingSpinner />;
  }

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card-header">
              <h5 className="card-title mb-0">
                {isEdit ? 'Edit Cycle' : 'Create New Cycle'}
              </h5>
            </div>
            <div className="card-body">
              <form onSubmit={handleSubmit}>
                <div className="row">
                  <div className="col-md-6 mb-3">
                    <label htmlFor="clientId" className="form-label">Client *</label>
                    <select
                      className={`form-select ${errors.clientId ? 'is-invalid' : ''}`}
                      id="clientId"
                      name="clientId"
                      value={formData.clientId}
                      onChange={handleChange}
                      disabled={isEdit} // Don't allow changing client in edit mode
                    >
                      <option value="">Select a client</option>
                      {clients.map(client => (
                        <option key={client.id} value={client.id}>
                          {client.name} - {client.phone}
                        </option>
                      ))}
                    </select>
                    {errors.clientId && <div className="invalid-feedback">{errors.clientId}</div>}
                  </div>

                  <div className="col-md-6 mb-3">
                    <label htmlFor="targetAmount" className="form-label">Target Amount (UGX) *</label>
                    <input
                      type="number"
                      className={`form-control ${errors.targetAmount ? 'is-invalid' : ''}`}
                      id="targetAmount"
                      name="targetAmount"
                      value={formData.targetAmount}
                      onChange={handleChange}
                      placeholder="Enter target amount"
                      min="0"
                      step="0.01"
                    />
                    {errors.targetAmount && <div className="invalid-feedback">{errors.targetAmount}</div>}
                  </div>

                  <div className="col-md-6 mb-3">
                    <label htmlFor="dailyContribution" className="form-label">Daily Contribution (UGX) *</label>
                    <input
                      type="number"
                      className={`form-control ${errors.dailyContribution ? 'is-invalid' : ''}`}
                      id="dailyContribution"
                      name="dailyContribution"
                      value={formData.dailyContribution}
                      onChange={handleChange}
                      placeholder="Enter daily contribution"
                      min="0"
                      step="0.01"
                    />
                    {errors.dailyContribution && <div className="invalid-feedback">{errors.dailyContribution}</div>}
                  </div>

                  <div className="col-md-6 mb-3">
                    <label htmlFor="startDate" className="form-label">Start Date *</label>
                    <input
                      type="date"
                      className={`form-control ${errors.startDate ? 'is-invalid' : ''}`}
                      id="startDate"
                      name="startDate"
                      value={formData.startDate}
                      onChange={handleChange}
                    />
                    {errors.startDate && <div className="invalid-feedback">{errors.startDate}</div>}
                  </div>

                  <div className="col-md-6 mb-3">
                    <label htmlFor="endDate" className="form-label">End Date *</label>
                    <input
                      type="date"
                      className={`form-control ${errors.endDate ? 'is-invalid' : ''}`}
                      id="endDate"
                      name="endDate"
                      value={formData.endDate}
                      onChange={handleChange}
                    />
                    {errors.endDate && <div className="invalid-feedback">{errors.endDate}</div>}
                  </div>
                </div>

                {/* Summary Section */}
                {formData.targetAmount && formData.dailyContribution && (
                  <div className="row">
                    <div className="col-12">
                      <div className="alert alert-info">
                        <h6>Cycle Summary:</h6>
                        <p className="mb-1">
                          <strong>Client:</strong> {getClientName(formData.clientId)}
                        </p>
                        <p className="mb-1">
                          <strong>Target Amount:</strong> {parseFloat(formData.targetAmount).toLocaleString()} UGX
                        </p>
                        <p className="mb-1">
                          <strong>Daily Contribution:</strong> {parseFloat(formData.dailyContribution).toLocaleString()} UGX
                        </p>
                        <p className="mb-0">
                          <strong>Expected Duration:</strong> {Math.ceil(parseFloat(formData.targetAmount) / parseFloat(formData.dailyContribution))} days
                        </p>
                      </div>
                    </div>
                  </div>
                )}

                <div className="d-flex gap-2">
                  <button 
                    type="submit" 
                    className="btn btn-primary"
                    disabled={loading}
                  >
                    {loading ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                        {isEdit ? 'Updating...' : 'Creating...'}
                      </>
                    ) : (
                      <>
                        <i className="fas fa-save me-2"></i>
                        {isEdit ? 'Update Cycle' : 'Create Cycle'}
                      </>
                    )}
                  </button>
                  <button 
                    type="button" 
                    className="btn btn-secondary"
                    onClick={() => navigate('/cycles')}
                    disabled={loading}
                  >
                    <i className="fas fa-times me-2"></i>
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CycleForm;
