import React, { useState } from 'react';

// Dummy data for demonstration
const dummyReports = [
  { id: 1, type: 'Deposit', date: '2024-06-01', user: 'john', amount: 1000 },
  { id: 2, type: 'Withdrawal', date: '2024-06-02', user: 'jane', amount: 500 },
  { id: 3, type: 'Commission', date: '2024-06-03', user: 'admin', amount: 200 },
];

const reportTypes = [
  { value: '', label: 'All Types' },
  { value: 'Deposit', label: 'Deposit' },
  { value: 'Withdrawal', label: 'Withdrawal' },
  { value: 'Commission', label: 'Commission' },
];

function exportToCSV(data, filename) {
  const csvRows = [];
  const headers = Object.keys(data[0]);
  csvRows.push(headers.join(','));
  for (const row of data) {
    csvRows.push(headers.map(h => JSON.stringify(row[h] ?? '')).join(','));
  }
  const csvContent = csvRows.join('\n');
  const blob = new Blob([csvContent], { type: 'text/csv' });
  const url = window.URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.setAttribute('hidden', '');
  a.setAttribute('href', url);
  a.setAttribute('download', filename);
  document.body.appendChild(a);
  a.click();
  document.body.removeChild(a);
}

const Reports = () => {
  const [filters, setFilters] = useState({
    type: '',
    startDate: '',
    endDate: '',
  });
  const [reports] = useState(dummyReports);
  const [loading] = useState(false); // For future async data

  // Filter logic (client-side for demo)
  const filteredReports = reports.filter(r => {
    const matchesType = !filters.type || r.type === filters.type;
    const matchesStart = !filters.startDate || r.date >= filters.startDate;
    const matchesEnd = !filters.endDate || r.date <= filters.endDate;
    return matchesType && matchesStart && matchesEnd;
  });

  const handleChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  const handleDownload = () => {
    if (filteredReports.length === 0) return;
    exportToCSV(filteredReports, 'report.csv');
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-12">
          <div className="d-flex justify-content-between align-items-center mb-4">
            <h1>Reports</h1>
          </div>

          <div className="card">
            <div className="card-header">
              <div className="row align-items-end">
                <div className="col-md-3">
                  <label className="form-label mb-0">Type</label>
                  <select
                    className="form-select"
                    name="type"
                    value={filters.type}
                    onChange={handleChange}
                  >
                    {reportTypes.map(rt => (
                      <option key={rt.value} value={rt.value}>{rt.label}</option>
                    ))}
                  </select>
                </div>
                <div className="col-md-3">
                  <label className="form-label mb-0">Start Date</label>
                  <input
                    type="date"
                    className="form-control"
                    name="startDate"
                    value={filters.startDate}
                    onChange={handleChange}
                  />
                </div>
                <div className="col-md-3">
                  <label className="form-label mb-0">End Date</label>
                  <input
                    type="date"
                    className="form-control"
                    name="endDate"
                    value={filters.endDate}
                    onChange={handleChange}
                  />
                </div>
                <div className="col-md-3 text-end">
                  <button
                    className="btn btn-primary"
                    onClick={handleDownload}
                    disabled={filteredReports.length === 0}
                  >
                    <i className="fas fa-download me-2"></i>
                    Download CSV
                  </button>
                </div>
              </div>
            </div>
            <div className="card-body">
              {loading ? (
                <div className="text-center">
                  <div className="spinner-border" role="status">
                    <span className="visually-hidden">Loading...</span>
                  </div>
                </div>
              ) : (
                <div className="table-responsive">
                  <table className="table table-striped">
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>Type</th>
                        <th>Date</th>
                        <th>User</th>
                        <th>Amount</th>
                      </tr>
                    </thead>
                    <tbody>
                      {filteredReports.length === 0 ? (
                        <tr>
                          <td colSpan="5" className="text-center">
                            No reports found
                          </td>
                        </tr>
                      ) : (
                        filteredReports.map((r) => (
                          <tr key={r.id}>
                            <td>{r.id}</td>
                            <td>{r.type}</td>
                            <td>{r.date}</td>
                            <td>{r.user}</td>
                            <td>{r.amount}</td>
                          </tr>
                        ))
                      )}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Reports;
